function shuffle(a){const b=[...a];for(let i=b.length-1;i>0;i--){const j=Math.floor(Math.random()*(i+1));[b[i],b[j]]=[b[j],b[i]];}return b;}

// Show first name only, add surname initial(s) if ambiguous
const displayName=(fullName,allNames)=>{
  if(!fullName)return"anonym";
  const parts=fullName.split(" ");
  const first=parts[0];
  const rest=parts.slice(1).join(" ");
  const sameFirst=(allNames||[]).filter(n=>n!==fullName&&n.split(" ")[0]===first);
  if(!sameFirst.length)return first;
  for(let i=1;i<=rest.length;i++){
    const suffix=rest.slice(0,i).toUpperCase()+".";
    const clash=sameFirst.some(n=>{const r=n.split(" ").slice(1).join(" ");return r.slice(0,i).toUpperCase()===rest.slice(0,i).toUpperCase();});
    if(!clash)return first+" "+suffix;
  }
  return fullName;
};

