  const [nameIn, setNameIn] = useState("");
  const [lastNameIn, setLastNameIn] = useState("");
  const [selectedEmoji, setSelectedEmoji] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const [assignedPw, setAssignedPw] = useState("");
  const [me, setMe] = useState({pw:"",name:"",emoji:""});
  const [users, setUsers] = useState({});
  const [polls, setPolls] = useState({});
  const [votes, setVotes] = useState({});
  const [tasks, setTasks] = useState([]);
  const [changes, setChanges] = useState({});
  const [voteLog, setVoteLog] = useState([]);
  const [err, setErr] = useState("");
  const [loading, setLoading] = useState(false);
  const [activePoll, setActivePoll] = useState(null);
  const [myVotes, setMyVotes] = useState({});
  const [saved, setSaved] = useState(false);
  const [showPw, setShowPw] = useState(false);
  const [newTaskTitle, setNewTaskTitle] = useState("");
  const [newTaskAssignee, setNewTaskAssignee] = useState("");
  const [showEndModal, setShowEndModal] = useState(false);
  const [saveStatus, setSaveStatus] = useState("idle"); // idle | saving | saved
  const saveTimerRef = useRef(null);
  const [toast, setToast] = useState("");
  const showToast=(msg)=>{setToast(msg);setTimeout(()=>setToast(""),3000);};

  const [adminMode, setAdminMode] = useState(false);
  const [viewAs, setViewAs] = useState("styrman"); // "styrman" | "sjoman"
  const isAdmin = adminMode && viewAs === "styrman";

  const loadAll = useCallback(async()=>{
    const [u,p,v,t,c,vl] = await Promise.all([ld(SK.users),ld(SK.polls),ld(SK.votes),ld(SK.tasks),ld(SK.changes),ld(SK.voteLog)]);
    setUsers(u||{});
    const pp=(p&&Object.keys(p).length>0)?p:DEFAULT_POLLS;
    if(!p||Object.keys(p).length===0) await sv(SK.polls,DEFAULT_POLLS);
    setPolls(pp); setVotes(v||{});
    setTasks(Array.isArray(t)?t:[]);
    setChanges(c||{});
    setVoteLog(Array.isArray(vl)?vl:[]);
    return {u:u||{},p:pp,v:v||{},c:c||{}};
  },[]);

  const setPhaseP=useCallback((p,poll=null)=>{
    setPhase(p);
    sv("svx2-phase",{phase:p,poll},false).catch(()=>{});
  },[]);

  useEffect(()=>{
    (async()=>{
      const rem=await ld(SK.remember,false);
      if(rem&&rem.pw){
        const{u}=await loadAll();
        const savedPhase=await ld("svx2-phase",false);
        if(rem.admin) setAdminMode(true); else setAdminMode(false);
        const found=Object.entries(u).find(([,d])=>d.pw===rem.pw);
        if(found){
          setMe({pw:rem.pw,name:found[0],emoji:found[1].emoji||""});
          if(savedPhase&&savedPhase.phase==="poll"&&savedPhase.poll){setActivePoll(savedPhase.poll);setPhase("poll_restore");}
          else setPhase("home");return;
        }
      }
      setPhase("welcome");
    })();
  },[loadAll]);

  useEffect(()=>{
    if(phase==="poll_restore"&&activePoll){openPoll(activePoll);}
  },[phase,activePoll]);

  const handleGate=()=>{
    if(sharedIn.toLowerCase().trim()===SHARED_PASSWORD.toLowerCase()){setPhase("register");setErr("");}
    else setErr("Fel lÃ¶senord! FrÃ¥ga Jesper.");
  };

  const handleLogin=async()=>{
    const pw=pwIn.trim();
    const first=sanitize(nameIn);const last=sanitize(lastNameIn);
    if(!first||!last){setErr("Ange fÃ¶r- och efternamn!");return;}
    setLoading(true);
    const{u}=await loadAll();
    const fullName=(first+" "+last).toLowerCase();
    const admin=isMaster(pw);
    if(admin){
      // Admin: find or create user account
      let userEntry=u[fullName];
      if(!userEntry){
        // Auto-register admin as user
        const avail=EASY_PASSWORDS.filter(p=>!Object.values(u).map(d=>d.pw).includes(p));
        if(!avail.length){setErr("Inga lÃ¶senord kvar!");setLoading(false);return;}
        const rndPw=avail[Math.floor(Math.random()*avail.length)];
        u[fullName]={pw:rndPw,emoji:"ðŸ¤™"};
        await sv(SK.users,u);setUsers(u);
        userEntry=u[fullName];
      }
      setMe({pw:userEntry.pw,name:fullName,emoji:userEntry.emoji||"ðŸ¤™"});
      setAdminMode(true);
      if(rememberMe) await sv(SK.remember,{pw:userEntry.pw,name:fullName,emoji:userEntry.emoji||"ðŸ¤™",admin:true},false);
      setPhase("home");setLoading(false);setErr("");return;
    }
    const low=sanitizePw(pw).toLowerCase();
    setAdminMode(false);
    if(!u[fullName]){setErr("Hittade inget konto fÃ¶r \""+first+" "+last+"\". Har du registrerat dig?");setLoading(false);return;}
    if(u[fullName].pw!==low){setErr("Fel lÃ¶senord!");setLoading(false);return;}
    const em=u[fullName].emoji||"";
    setMe({pw:low,name:fullName,emoji:em});
    if(rememberMe) await sv(SK.remember,{pw:low,name:fullName,emoji:em},false);
    setPhase("home");setLoading(false);setErr("");
  };

  const handleRegister=async()=>{
    const first=sanitize(nameIn);const last=sanitize(lastNameIn);
    if(!first||!last){setErr("Skriv fÃ¶r- och efternamn!");return;}
    if(!selectedEmoji){setErr("VÃ¤lj en emoji!");return;}
    const fullName=(first+" "+last).toLowerCase();
    setLoading(true);
    const u=await ld(SK.users);
    if(u[fullName]){setErr("\""+first+" "+last+"\" Ã¤r redan registrerat! GÃ¥ tillbaka och logga in.");setLoading(false);return;}
    const used=Object.values(u).map(d=>d.pw);
    const avail=shuffle(EASY_PASSWORDS.filter(p=>!used.includes(p)));
    if(!avail.length){setErr("Inga lÃ¶senord kvar! Be Jesper.");setLoading(false);return;}
    const pw=avail[0];
    u[fullName]={pw,emoji:selectedEmoji};
    await sv(SK.users,u);setUsers(u);
    setAssignedPw(pw);setMe({pw,name:fullName,emoji:selectedEmoji});
    setPhase("showPassword");setLoading(false);setErr("");await autoBackup();
  };

  const enterFromPw=async()=>{
    if(rememberMe) await sv(SK.remember,me,false);
    await loadAll();setPhase("home");
  };

  const logout=async()=>{
    try{await fetch(`/api/storage?key=${encodeURIComponent(SK.remember)}&shared=false`,{method:"DELETE"});await fetch(`/api/storage?key=svx2-phase&shared=false`,{method:"DELETE"});}catch(e){}
    setMe({pw:"",name:"",emoji:""});setPhase("welcome");setSharedIn("");setPwIn("");setNameIn("");setLastNameIn("");setSelectedEmoji("");setLoginMode(null);
  };

  const openPoll=async(id)=>{
    const[v,c]=await Promise.all([ld(SK.votes),ld(SK.changes)]);
    setVotes(v||{});setChanges(c||{});
    const pv=(v||{})[id]||{};
    const myV=pv[me.pw];
    if(myV&&typeof myV==="object"&&Object.keys(myV).length>0){
      setMyVotes(myV);setSaved(true);
    } else {
      setMyVotes({});setSaved(false);
    }
    setActivePoll(id);setPhase("poll");sv("svx2-phase",{phase:"poll",poll:id},false).catch(()=>{});
  };

  const doSave=useCallback(async(votesObj)=>{
    setSaveStatus("saving");
    const[v,c,vl]=await Promise.all([ld(SK.votes),ld(SK.changes),ld(SK.voteLog)]);
    if(!v[activePoll])v[activePoll]={};
    const oldVotes=v[activePoll][me.pw]||{};
    const had=typeof oldVotes==="object"&&Object.keys(oldVotes).length>0;
    v[activePoll][me.pw]=votesObj;
    await sv(SK.votes,v);setVotes(v);
    // Log changed votes
    const log=Array.isArray(vl)?[...vl]:[];
    Object.entries(votesObj).forEach(([optId,vote])=>{
      if(oldVotes[optId]!==vote){
        log.push({ts:new Date().toISOString(),who:me.name,pollId:activePoll,optId,vote});
      }
    });
    if(log.length>200) log.splice(0,log.length-200); // keep last 200
    await sv(SK.voteLog,log);setVoteLog(log);
    if(had){
      if(!c[activePoll])c[activePoll]={};
      c[activePoll][me.pw]=(c[activePoll][me.pw]||0)+1;
      await sv(SK.changes,c);setChanges(c);
    }
    setSaved(true);setSaveStatus("saved");await autoBackup();
    if(saveTimerRef.current)clearTimeout(saveTimerRef.current);
    saveTimerRef.current=setTimeout(()=>setSaveStatus("idle"),2000);
  },[activePoll,me.pw,me.name]);

  const handleVoteClick=(optId,voId)=>{
    const next={...myVotes,[optId]:voId};
    setMyVotes(next);setSaved(false);
    doSave(next);
  };

  const decidePoll=async(pid,oid)=>{const p=await ld(SK.polls);if(p[pid]){p[pid].status="decided";p[pid].decidedOption=oid;}await sv(SK.polls,p);setPolls(p);};
  const reopenPoll=async(pid)=>{const p=await ld(SK.polls);if(p[pid]){p[pid].status="open";p[pid].decidedOption=null;}await sv(SK.polls,p);setPolls(p);};

  const addTask=async()=>{if(!newTaskTitle.trim())return;const t=[...tasks];t.push({id:Date.now().toString(),title:newTaskTitle.trim(),assignee:newTaskAssignee.trim(),status:"pending"});setTasks(t);setNewTaskTitle("");setNewTaskAssignee("");sv(SK.tasks,t);};
  const toggleTask=async(id)=>{const t=tasks.map(tk=>tk.id===id?{...tk,status:tk.status==="done"?"pending":"done"}:tk);setTasks(t);sv(SK.tasks,t);};
  const deleteTask=async(id)=>{const t=tasks.filter(tk=>tk.id!==id);setTasks(t);sv(SK.tasks,t);};

  const autoBackup=async()=>{
    try{
      const[u,p,v,t,c]=await Promise.all([ld(SK.users),ld(SK.polls),ld(SK.votes),ld(SK.tasks),ld(SK.changes)]);
      const listRes=await(await fetch("/api/storage/list?prefix=svx2-auto-&shared=true")).json();
      const keys=(listRes&&listRes.keys)?listRes.keys.sort():[];
      if(keys.length>=10){for(let i=0;i<=keys.length-10;i++){try{await fetch(`/api/storage?key=${encodeURIComponent(keys[i])}&shared=true`,{method:"DELETE"});}catch(e){}}}
      await sv("svx2-auto-"+Date.now(),{users:u,polls:p,votes:v,tasks:t,changes:c,voteLog:vl||[],ts:new Date().toISOString()});
    }catch(e){}
  };

  const [showNuke, setShowNuke] = useState(false);
  const [nukeConfirmPw, setNukeConfirmPw] = useState("");
  const [nukeErr, setNukeErr] = useState("");
  const [nukeStatus, setNukeStatus] = useState("");

  const nukeAll=async()=>{
    if(!isMaster(nukeConfirmPw)){setNukeErr("Fel lÃ¶senord!");return;}
    setNukeStatus("raderar");
    await Promise.all([sv(SK.users,{}),sv(SK.polls,{}),sv(SK.votes,{}),sv(SK.tasks,[]),sv(SK.changes,{}),sv(SK.voteLog,[])]);
    await loadAll();
    setNukeStatus("klart");setNukeConfirmPw("");
    setTimeout(()=>{setShowNuke(false);setNukeStatus("");setNukeErr("");},1500);
  };

  const [exportData, setExportData] = useState(null);

  const exportAll=async()=>{
    const[u,p,v,t,c,vl]=await Promise.all([ld(SK.users),ld(SK.polls),ld(SK.votes),ld(SK.tasks),ld(SK.changes),ld(SK.voteLog)]);
    setExportData(JSON.stringify({users:u,polls:p,votes:v,tasks:t,changes:c,voteLog:vl,exportedAt:new Date().toISOString()},null,2));
  };

